dynaForm.JS:
var newSite=function(form)
{
	var nameSite=form['form[NW_TITLE]'].value || '';
	if(nameSite.trim()=='')
	{
		new leimnud.module.app.alert().make({
			label:'Invalid name'
		});
	}
	else
	{
		var arg = new leimnud.dom.serializer(form,false);
		var r = new leimnud.module.rpc.xmlhttp({
			url		: form.action,
			method	: form.method,
			args	: arg.form()
		});
		//r.callback=this.callback || false;
		r.make();
	}
}
