dynaForm.JS:
function triggerNew()
{
  popupWindow('@G::LoadTranslation(ID_NEW_TRIGGERS)', '@G::encryptlink(@#triggersEdit)?PRO_UID=@%PRO_UID' , 700, 500);
}

function triggerEdit(sUID)
{
  popupWindow('@G::LoadTranslation(ID_EDIT_TRIGGERS)', '@G::encryptlink(@#triggersEdit)?TRI_UID=' + sUID , 700, 500);
}

function triggerSave(form)
{
  ajax_post(form.action, form, 'POST');
  currentPopupWindow.remove();
  @#PAGED_TABLE_ID.refresh();
}

function triggerDelete(sUID)
{
  new leimnud.module.app.confirm().make({
    label:'@G::LoadTranslation(ID_MSG_CONFIRM_DELETE_TRIGGER)',
    action:function()
    {
      ajax_function('@G::encryptlink(@#triggersDelete)', '', 'TRI_UID=' + sUID, 'POST');
      @#PAGED_TABLE_ID.refresh();
    }.extend(this)
  });
}
