dynaForm.js:
function addGroup(){
  @#PAGED_TABLE_ID.updateField('ds', 'Add new group', 500, 170);
}
function saveGroup( form ) {
  ajax_post( form.action, form, 'POST');
  currentPopupWindow.remove();
  @#PAGED_TABLE_ID.refresh();
}
function saveUserGroup( form ) {
  if (!currentGroup) return ;
  ajax_post( form.action + '?GRP_UID=' + currentGroup, form , 'POST' );
  currentPopupWindow.remove();
  refreshTree();
  @#PAGED_TABLE_ID.refresh();
}
